  const emotion = require("@emotion/react");

  const legacyShareScope = {
      react: {
        [react.version]: {
          get: () => new Promise(resolve => resolve(() => react)),
          loaded: true,
          from: "webpack4",
        },
      },
      "react-dom": {
        [reactDom.version]: {
          get: () => new Promise(resolve => resolve(() => reactDom)),
          loaded: true,
          from: "webpack4",
        },
      },
      recoil: {
        [recoil]: {
          get: () => new Promise(resolve => resolve(() => recoil)),
          loaded: true,
          from: "webpack4",
        },
      },
      "@emotion/react": {
        [emotion]: {
          get: () => new Promise(resolve => resolve(() => emotion)),
          loaded: true,
          from: "webpack4",
        },
      },
    };

    // const container = window[scope]; // or get the container somewhere else

    // Initialize the container, it may provide shared modules
    await window[scope].init(legacyShareScope);
    const factory = await window[scope].get(module);
    const Module = factory();
    return Module;
  };
}

