import { useEffect, useState, lazy, Suspense } from "react";
import './App.css';

const useDynamicScript = (url) => {
  const [ready, setReady] = useState(false);
  const [failed, setFailed] = useState(false);

  useEffect(() => {
    if(!url) return;

    const element = document.createElement("script");

    element.src = url;
    element.type = "text/javascript";
    element.async = true;

    setReady(false);
    setFailed(false);

    element.onload = () => {
      console.log(`Dynamic Script Loaded: ${url}`);
      setReady(true);
    };

    element.onerror = () => {
      console.error(`Dynamic Script Error: ${url}`);
      setReady(false);
      setFailed(true);
    };

    document.head.appendChild(element);

    return () => {
      console.log(`Dynamic Script Removed: ${url}`);
      document.head.removeChild(element);
    };
  }, [url])

  return {
    ready,
    failed
  };
};

const RemoteReactComponent = ({ url, scope, modul, ...props }) => {
  const { ready, failed } = useDynamicScript(url);

  if(!ready) return <h2>Loading dynamic script: {url}</h2>;

  if(failed) return <h2>Failed to load dynamic script: {url}</h2>;

  const Component = lazy(
    async () => {
      const container = window[scope];
      const factory = await container.get(module);
      const Module = factory();
      return Module;
    }
  );

  return (
    <Suspense fallback="Loading System">
      <Component {...props} />
    </Suspense>
  )
}

function App() {
  return (
    <div style={{
      padding: 10,
    }}>
      <RemoteReactComponent 
        url="http://localhost:3001/graph1/remoteEntry.js"
        module="graph1mod"
        scope="graph1scope"
      />
    </div>
  );
}

export default App;
