import React, { useEffect, useState, lazy, Suspense } from "react";
import './App.css';
import { RecoilRoot, atom, useRecoilState } from "recoil";
import { ThemeProvider } from '@emotion/react';

// function loadComponent(scope, module) {
//   return async () => {
//     // Initializes the shared scope. Fills it with known provided modules from this build and all remotes
//     await __webpack_init_sharing__('default');
//     const container = window[scope]; // or get the container somewhere else
//     // Initialize the container, it may provide shared modules
//     await container.init(__webpack_share_scopes__.default);
//     const factory = await window[scope].get(module);
//     const Module = factory();
//     return Module;
//   };
// }

const useDynamicScript = (args) => {
  const [ready, setReady] = useState(false);
  const [failed, setFailed] = useState(false);

  useEffect(() => {
    if(!args.url) return;

    const element = document.createElement("script");

    element.src = args.url;
    element.type = "text/javascript";
    element.async = true;

    setReady(false);
    setFailed(false);

    element.onload = () => {
      console.log(`Dynamic Script Loaded: ${args.url}`);
      setReady(true);
    };

    element.onerror = () => {
      console.error(`Dynamic Script Error: ${args.url}`);
      setReady(false);
      setFailed(true);
    };

    document.head.appendChild(element);

    return () => {
      console.log(`Dynamic Script Removed: ${args.url}`);
      document.head.removeChild(element);
    };
  }, [args.url])

  return {
    ready,
    failed
  };
};

const RemoteReactComponent = ({ url, module: targetModule, scope, ...props }) => {

  console.log('props :>> ', props);

  const { state } = props;

  console.log('state :>> ', state);

  const { ready, failed } = useDynamicScript({
    url,
  });

  // if (ready) {
  //   const o = global["webpack_require"] ? global["webpack_require"].o : {};
  //   console.log('scope :>> ', scope);

  //   window["graph1"].override(
  //     Object.assign(
  //       {
  //         react: () => Promise.resolve().then(() => () => require("react")),
  //       },
  //       o
  //     )
  //   );
  // }

  if (!ready) return <h2>Loading dynamic script: {url}</h2>;

  if (failed) return <h2>Failed to load dynamic script: {url}</h2>;

  const Component = lazy(
    () =>
      new Promise((resolve) => {
        const moduleResolve = resolve;
        const react = require("react");
        const legacyShareScope = {
          react: {
            [react.version]: {
              get: () => new Promise((resolve) => resolve(() => react)),
              loaded: true,
              from: "webpack4",
            },
          },
        };
        new Promise((resolve) => {
          resolve(window[scope].init(legacyShareScope));
        }).then(() => {
          window[scope].get(targetModule).then((factory) => {
            moduleResolve(factory());
          });
        });
      })
  );

  return (
    <Suspense fallback="Loading System">
      <Component {...props} />
    </Suspense>
  )
}

const theme = {
  colors: {
    primary: "hotpink",
  },
};

const DashboardWidget = ({ name, ...props }) => {
  return (<RemoteReactComponent 
    url={`http://localhost:3001/${name}/remoteEntry.js`}
    module={name}
    scope={name}
    {...props}
  />);
}

const graphState = atom({
  key: "graphState",
  default: 14,
});

console.log('graphState :>> ', graphState);

function Dashboard() {
  const [graph, ] = useRecoilState(graphState);

  console.log('graph Dash:>> ', graph);

  return (
    <div style={{
      padding: 10,
    }}>
      <div style={{ fontSize: "xx-large" }}>Data: {JSON.stringify(graph)}</div>
      <DashboardWidget 
        name="graph1" 
        state={graph} 
      />
    </div>
  );
}

function App() {
  return (
    <ThemeProvider theme={theme}>
      <RecoilRoot>
        <Dashboard />
      </RecoilRoot>
    </ThemeProvider>
  );
}

export default App;
